public class Exam2 {
 String exit(int number){
        switch((number - 1) / 13) { 
            case 0 : return "春";
            case 1 : return "夏";
            case 2 : return "秋";
            default: return "冬";
        }
    }

 public static void main(String args[]) { 
             Exam2 ex=new Exam2();
             System.out.printf(ex.exit(0));
        }
    }
